															SET _rowsub = TRUE;
															OPEN CR_subactividad;
																	Loop_sub:LOOP
																			FETCH CR_subactividad INTO _detalleactividad_id,_detallesubactividad_costosinIva,_detallesubactividad_porc;
																			IF _rowsub THEN
																				LEAVE Loop_sub;
